                .productsQuickView .modal-dialog .modal-content .products-content .product-meta span a:hover, .woocommerce ul.products li.product h3 a:hover, .woocommerce ul.products li.product .add-to-cart-btn, .woocommerce div.product .woocommerce-tabs ul.tabs li.active a, .products_details div.product .woocommerce-tabs ul.tabs li a:hover, .products_details div.product .woocommerce-tabs ul.tabs li.active, .products_details div.product .woocommerce-tabs ul.tabs li.active a:hover, .products_details div.product .product_meta span.posted_in a:hover, .products_details div.product .product_meta span.tagged_as a:hover, .products_details div.product span.price, .cart-table table tbody tr td.product-name a, .woocommerce-message::before, .woocommerce-info::before { color: '.esc_attr ($primary_color).'; }
                .woocommerce-info, .woocommerce-message { border-top-color: '.esc_attr ($primary_color).'; }
                .shop-sidebar ul li a:hover { color: '.esc_attr ($primary_color).' !important; }
                .woocommerce ul.products li.product:hover .add-to-cart-btn, .form-control:focus, .woocommerce .form-control:focus { border-color: '.esc_attr ($primary_color).'; }

                .footer-area { background-color: '.esc_attr( $footer_bg ).'; }
                .footer-bottom-area {  background-color: '.esc_attr( $footer_bottom_bg ).';  }
                #learn-press-profile-nav .tabs > li ul li a:hover { color: #fff !important; }
                .become-instructor-partner-content.bg-color .default-btn .icon-arrow { color: #252525 !important; }
                .become-instructor-partner-content.bg-color .default-btn:focus .icon-arrow, .become-instructor-partner-content.bg-color .default-btn:hover .icon-arrow { color: #ffffff !important; } 
                .entry-meta ul li a { text-transform: capitalize;}
            
                .top-header, .top-header-style-four { background-color: '.esc_attr( $top_header_color ).'; }
                .blog-details .blog-details-content .article-author { clear: both; }
                .edali-nav .navbar, .navbar-style-three .edali-nav, .navbar-area.header-sticky .edali-nav, .top-header-style-two { background-color: '.esc_attr( $header_bg_color ).'; }
                
                .cart-link span { background-color: '.esc_attr( $cart_icon_bg ).'; }
                ';
        
            // Hide Sticky Header
            if(isset($edali_opt['enable_sticky_header']) && $edali_opt['enable_sticky_header'] == false){ $custom_style .='
                .navbar-area.is-sticky{
                    display:none !important;
                }';
            }

            if( !isset($edali_opt['primary_color'])):
                $custom_style .='
                .footer-bottom-area .logo {
                    margin-bottom: 0;
                }
                .page-title-area {
                    padding-top: 180px;
                }';

            endif;

            // Blog hide banner
            if( $hide_blog_banner == true ):
                $custom_style .='
                .blog-area.ptb-100, .blog-details-area {
                    padding-top: 230px;
                }
             ';
            endif;            

            // Bubble Background Animation
            if( isset($edali_opt['hide_animation'] ) && !empty($edali_opt['hide_animation']) ):
                if($edali_opt['hide_animation'] == 'hide'):
                    $custom_style .='
                        #particles-js-circle-bubble-4, #particles-js-circle-bubble, #particles-js-circle-bubble-2, #particles-js-circle-bubble-3 {display:none}                    
                    ';
                endif;
            endif;


            if( isset($edali_opt['hide_animation_form_mobile'] ) && !empty($edali_opt['hide_animation_form_mobile']) ):
                if($edali_opt['hide_animation_form_mobile'] == 'hide'):
                    $custom_style .='
                        @media only screen and (max-width: 767px) {
                            #particles-js-circle-bubble-4, #particles-js-circle-bubble, #particles-js-circle-bubble-2, #particles-js-circle-bubble-3 {display:none} 
                        }                   
                    ';
                elseif($edali_opt['hide_animation_form_mobile'] == 'show'):
                    $custom_style .='
                        @media only screen and (max-width: 767px) {
                            #particles-js-circle-bubble-4, #particles-js-circle-bubble, #particles-js-circle-bubble-2, #particles-js-circle-bubble-3 {display:unset} 
                        }                                      
                    ';  
                endif;
            endif;

            // Image Animation
            if( isset($edali_opt['hide_image_animation'] ) && !empty($edali_opt['hide_image_animation']) ):
                if($edali_opt['hide_image_animation'] == 'hide'):
                    $custom_style .='
                    .hero-banner-image img, .single-categories-courses-item .icon, .single-offer-box .icon, .single-categories-courses-box:focus .icon, .single-categories-courses-box:hover .icon {animation:none}                    
                    ';
                endif;
            endif;

            if( isset($edali_opt['hide_image_animation_form_mobile'] ) && !empty($edali_opt['hide_image_animation_form_mobile']) ):
                if($edali_opt['hide_image_animation_form_mobile'] == 'hide'):
                    $custom_style .='
                        @media only screen and (max-width: 767px) {
                            .hero-banner-image img, .single-categories-courses-item .icon, .single-offer-box .icon, .single-categories-courses-box:focus .icon, .single-categories-courses-box:hover .icon {animation:none} 
                        }                   
                    ';
                else:
                    $custom_style .='
                        @media only screen and (max-width: 767px) {
                            .hero-banner-image img, .single-offer-box .icon {animation:border-transform 10s linear infinite alternate forwards;-webkit-animation:border-transform 10s linear infinite alternate forwards} 
                            .single-categories-courses-item .icon, .single-offer-box .icon, .single-categories-courses-box:focus .icon, .single-categories-courses-box:hover .icon {-webkit-animation:border-transform 10s linear infinite alternate forwards; animation: border-transform 10s linear infinite alternate forwards}
                        }                                      
                    ';  
                endif;
            endif;

            // Custom Css
            if( isset($edali_opt['css_code'] ) && !empty($edali_opt['css_code']) ):
                $custom_style .= $edali_opt['css_code'];
            endif;

            if( is_user_logged_in() ){ 
                $custom_style .=' .comments-area .comment-respond .form-submit {
                    margin-top: 20px;
                }';
            }
            
            wp_add_inline_style('edali-main-style', $custom_style);

            // Custom Js
            $custom_script ='';
            if( isset($edali_opt['js_code'] )){
                $custom_script .= $edali_opt['js_code'];
            }
            
            wp_add_inline_script( 'edali-main', $custom_script );
    }
}
add_action( 'wp_enqueue_scripts', 'edali_custom_style' );